    {
     "type": "radiogroup",
     "name": "screeningFormComplete",
     "visible": false,
     "readOnly": true,
     "choices": [
      {
       "value": "YPbNgujgGgCT0oGFk",
       "text": "Yes"
      },
      {
       "value": "EemClNEln3JUtUUii",
       "text": "No"
      }
     ],
     "surveyVariable": "CustomVariable:XAMXapRLngtw6srwm"
    },
    {
     "type": "text",
     "name": "firstName",
     "title": "First Name",
     "isRequired": true,
     "readOnly": true,
     "surveyVariable": "ParticipantVariable:firstName"
    },
    {
     "type": "text",
     "name": "lastName",
     "startWithNewLine": false,
     "title": "Last Name",
     "isRequired": true,
     "readOnly": true,
     "surveyVariable": "ParticipantVariable:lastName"
    },
    {
     "type": "text",
     "name": "globalID",
     "visible": false,
     "title": "globalID",
     "surveyVariable": "ParticipantVariable:globalId"
    },
    {
     "type": "text",
     "name": "customID",
     "visible": false,
     "title": "customID",
     "surveyVariable": "StudyInfoVariable:customId"
    },
    {
     "type": "expression",
     "name": "standard_sublink_english",
     "visible": false,
     "expression": "\"https://hbcd.ripplescience.com/survey/e2dAHPRgEXfiZvnF6/rvbixAHbGj2Cft8gX/form?globalID=\" + {globalID} + \"&firstName=\" + {firstName} + \"&lastName=\" + {lastName} + \"&customID=\" + {customID} + \"&fromInternal=\" + {fromInternal}"
    },
    {
     "type": "expression",
     "name": "standard_sublink_spanish",
     "visible": false,
     "expression": "\"stan sp\""
    },
    {
     "type": "expression",
     "name": "alternate_sublink_english",
     "visible": false,
     "expression": "\"alt eng\""
    },
    {
     "type": "expression",
     "name": "alternate_sublink_spanish",
     "visible": false,
     "expression": "\"alt sp\""
    },
    {
     "type": "radiogroup",
     "name": "selected_consent_type",
     "title": "Please select which type of consent you need:",
     "choices": [
      {
       "value": "standard_en",
       "text": "Standard English Consent"
      },
      {
       "value": "standard_sp",
       "text": "Standard Spanish Consent"
      },
      {
       "value": "alternate_en",
