{
 "pages": [
  {
   "name": "page1",
   "elements": [
    {
     "type": "html",
     "name": "question2",
     "html": "<p style=\"text-align:left;color:black;font-size:16px\">Please use this QR code to access the LORIS profile that corresponds to this participant. Use the “<u>Begin Participant Workflow</u>” button on the LORIS homepage. You will need to scan the code again each time you access an instrument in LORIS. <b>The Child QR code is used for visits V02-V10!</b></p>\n\n<p style=\"text-align:left;color:black;font-size:16px\">Note: You may have to wait up to 15 minutes after profile creation Step 2 for your participant's QR code to be available.</p>"
    },
    {
     "type": "text",
     "name": "firstName",
     "title": "First Name",
     "isRequired": true,
     "readOnly": true,
     "surveyVariable": "ParticipantVariable:firstName"
    },
    {
     "type": "text",
     "name": "lastName",
     "startWithNewLine": false,
     "title": "Last Name",
     "isRequired": true,
     "readOnly": true,
     "surveyVariable": "ParticipantVariable:lastName"
    },
    {
     "type": "dropdown",
     "name": "visit_number",
     "title": "Which visit are you completing?",
     "choices": [
      {
       "value": "V02",
       "text": "Visit 02"
      },
      {
       "value": "V03",
       "text": "Visit 03"
      }
     ]
    },
    {
     "type": "text",
     "name": "globalID",
     "visible": false,
     "title": "globalID",
     "surveyVariable": "ParticipantVariable:globalId"
    },
    {
     "type": "expression",
     "name": "QR_URL",
     "visible": false,
     "title": "QR URL",
     "expression": "\"https://integration.hbcd.ahc.umn.edu/qr/\"+{globalID}+\"/\"+{visit_number}"
    },
    {
     "type": "radiogroup",
     "name": "step2Complete",
     "visible": false,
     "readOnly": true,
     "choices": [
      {
       "value": "oo5qh6MSwg53QtooL",
       "text": "Yes"
      },
      {
       "value": "0nZHjqEdQfTbOX-TP",
       "text": "No"
      }
     ],
     "surveyVariable": "CustomVariable:b5jNRm8gtMkHtkL6B"
    }
   ],
   "visibleIf": "{step2Complete} = 'oo5qh6MSwg53QtooL'"
  },
  {
   "name": "page2",
   "elements": [
    {
     "type": "html",
     "name": "question1",
     "html": "<a style=\"font-size:50px\" href=\"{QR_URL}\" target=\"_blank\">Open QR Code</a>"
    }
   ],
   "visibleIf": "{visit_number} notempty"
  },
  {
   "name": "page3",
   "elements": [
    {
     "type": "html",
     "name": "question4",
     "html": "<p style=\"text-align:left;color:black;font-size:20px\">It appears you have not completed the Create Child Profile (Step 2) survey. Please complete Step 2 before continuing.</p>"
    }
   ],
   "visibleIf": "{step2Complete} <> 'oo5qh6MSwg53QtooL'"
  }
 ],
 "clearInvisibleValues": "none"
}

